mapper.select:
select prod_id, cate_id, prod_name, prod_price, prod_amount, prod_discount, prod_maker, prod_content, prod_uploadfolder, prod_img, prod_forsale, prod_regdate, prod_updatedate, prod_reviewcount, prod_wishcount, prod_inquirycount from product_tbl where prod_forsale = 'Y' and cate_id in
#{cate_id}
order by prod_id desc limit #{cri.pageStart}, #{cri.perPageNum}

SELECT count(*)
FROM product_tbl
WHERE prod_forsale = 'Y' AND cate_id = #{cate_id}

mapper.update:
update
    product_tbl
set
    prod_amount = prod_amount - #{prod_amount}
where
    prod_id = #{prod_id}

update
    product_tbl
set
    prod_ordercount = prod_ordercount + #{prod_amount}
where
    prod_id = #{prod_id}
